import { data, err, error, load, loading, text } from "../Action/actionType";
export function loginreducer(
  state = {
    loading: false,
    data: [],
    error: false,
  },
  action
) {
  if (action.type === loading) {
    return {
      ...state,
      loading: true,
    };
  } else if (action.type === data) {
    return {
      ...state,
      data: action.payload,
    };
  } else if (action.type === error) {
    return {
      ...state,
      error: true,
    };
  } else if (action.type === err) {
    return {
      ...state,
      error: false,
    };
  } else if (action.type === load) {
    return {
      ...state,
      loading: false,
    };
  } else {
    return state;
  }
}

export function loginTextreducer(state = false, action) {
  if (action.type === text) {
    return !state;
  } else {
    return state;
  }
}
