import { data, error, loading } from "../Action/actionType";

export function loginreducer(
  state = {
    loading: false,
    data: [],
    error: false,
  },
  action
) {
  if (action.type === loading) {
    return {
      ...state,
      loading: true,
    };
  } else if (action.type === data) {
    return {
      ...state,
      data: action.payload,
    };
  } else if (action.type === error) {
    return {
      ...state,
      error: true,
    };
  } else {
    return state;
  }
}
