import React, { useEffect, useState } from "react";
const ListComp = () => {
  const [Apidata, setData] = useState();

  const fetchApi = async (url) => {
    try {
      const response = await fetch(url);
      const data = await response.json();
      setData(data);
    } catch (error) {
      console.log(error);
    }
  };
  //   function fetchApi(url) {
  //     fetch(url)
  //       .then((data) => {
  //         console.log(data);
  //         return data.json();
  //       })
  //       .then((item) => {
  //         console.log(item);
  //       });
  //   }
  useEffect(() => {
    let url = "https://api.minebrat.com/api/v1/states";
    fetchApi(url);
  }, []);
  function cityHandler(id) {
    console.log(id);
  }

  return (
    <>
      {console.log(Apidata)}

      {!Apidata && <h1>loading...</h1>}
      {Apidata && (
        <ul>
          {Apidata.map((eachItem) => {
            const { stateId, stateName, city } = eachItem;
            console.log(city);
            return (
              <li key={stateId}>
                <h1>{stateId}</h1>
                <h1>{stateName}</h1>
                <ul>
                  <select>
                    {city &&
                      city.map((item) => {
                        return (
                          <option
                            onClick={() => {
                              cityHandler(item.cityId);
                            }}
                          >
                            {item.cityName}
                          </option>
                        );
                      })}
                  </select>
                </ul>
                <input type="radio" />
              </li>
            );
          })}
        </ul>
      )}
    </>
  );
};
export default ListComp;
