import React, { useEffect, useState } from "react";
import PrintDetails from "./PrintDetails";
import { useNavigate } from "react-router-dom";
const ListComp = () => {
  const [Apidata, setData] = useState();
  const [cityData, setCityData] = useState("");
  const [stateData, setStateData] = useState("");

  const fetchApi = async (url) => {
    try {
      const response = await fetch(url);
      const data = await response.json();
      setData(data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    let url = "https://api.minebrat.com/api/v1/states";
    fetchApi(url);
  }, []);
  function cityHandler(e) {
    setCityData(e.target.value);
  }
  let navigate = useNavigate();
  function submithandler(abc) {
    setStateData(abc);
    setTimeout(() => {
      navigate("/print", { state: { cityData, stateData } });
    }, 1000);
  }

  return (
    <>
      {console.log(Apidata)}
      {!Apidata && <h1>loading...</h1>}
      {Apidata && (
        <ul>
          {Apidata.map((eachItem) => {
            const { stateId, stateName, city } = eachItem;
            return (
              <li key={stateId}>
                <h1>{stateId}</h1>
                <h1>{stateName}</h1>
                <select
                  onChange={() => {
                    cityHandler(stateName);
                  }}
                >
                  {city &&
                    city.map((item) => {
                      return (
                        <option value={item.cityName}>{item.cityName}</option>
                      );
                    })}
                </select>
                <button
                  onClick={() => {
                    submithandler(stateName);
                  }}
                >
                  submit
                </button>
                {/* {!cityData && <h1>wait for while......</h1> }
                {cityData && } */}
                <hr />

                <input type="radio" />
              </li>
            );
          })}
        </ul>
      )}
    </>
  );
};
export default ListComp;
