import React, { useEffect } from "react";
import Button from "./Button";
import { useDispatch, useSelector } from "react-redux";
import { loginData, handleText } from "./reducer/Action";
const LoginPage = () => {
  const container = {
    display: "flex",
  };
  const x = useSelector((item) => {
    return item.loginreducer;
  });
  const y = useSelector((item) => {
    return item.loginTextreducer;
  });
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(loginData());
  }, [dispatch]);
  const list = {
    width: "1000px",
    height: "600px",
    display: "flex",
    flexDirection: "row",
    flexWrap: "Wrap",
    rowGap: "2px",
    columnGap: "2px",
    listStyleType: "None",
  };
  const item = {
    width: "400px",
    height: "150px",
    border: "2px solid black",
    padding: "20px",
  };
  const btnstyle = {
    position: "fixed",
    left: "0",
  };
  const handleSelect = (id, event) => {
    let z = x.data.filter((item) => {
      console.log("Hello");
      return id === item.id;
    });
    if (z.id === id) {
      dispatch(handleText());
    }
    console.log(z);
    console.log(id);
    console.log(event.target.checked);
  };
  return (
    <>
      {x.loading ? (
        <h2 style={{ color: "red" }}>Loading Data </h2>
      ) : (
        <div style={container}>
          <ul style={list}>
            {x.data
              .map(({ id, name, email }) => {
                return (
                  <li key={id} style={item}>
                    <h2>userId : {name}</h2>
                    <h4>title : {email}</h4>
                    <input
                      key={id}
                      type="checkbox"
                      checked={y}
                      style={{ display: "inline-block" }}
                      onChange={(event) => handleSelect(email, event)}
                    />
                  </li>
                );
              })
              .splice(0, 50)}
            <li>
              <button>Submit</button>
            </li>
          </ul>
          <Button style={btnstyle} />
        </div>
      )}
    </>
  );
};
export default LoginPage;
