import React, { useEffect, useReducer } from "react";
import "./products.css";
const reducer = (state, action) => {
  if (action.type === "SETDATA") {
    return {
      ...state,
      data: action.payload,
    };
  } else if (action.type === "SETLOADING") {
    return {
      ...state,
      loading: action.payload,
    };
  } else if (action.type === "SETERROR") {
    return {
      ...state,
      error: {
        ...state.error,
        isError: action.payload,
      },
    };
  } else if (action.type === "SETLOAD") {
    return {
      ...state,
      loading: action.payload,
    };
  } else if (action.type === "SETERR") {
    return {
      ...state,
      error: {
        ...state.error,
        isError: action.payload.isError,
        msg: action.payload.msg,
      },
    };
  }
  return state;
};
const Products = () => {
  const initalData = {
    data: null,
    loading: false,
    error: {
      isError: false,
      msg: "",
    },
  };
  const [state, dispatch] = useReducer(reducer, initalData);
  const fetchApi = async (url) => {
    dispatch({ type: "SETLOADING", payload: true });
    dispatch({ type: "SETERROR", payload: false });
    try {
      const respone = await fetch(url);
      const data = await respone.json();
      dispatch({ type: "SETDATA", payload: data });
      dispatch({ type: "SETLOAD", payload: false });
      if (respone.status === 404) {
        throw new Error(" data not found ");
      }
    } catch (error) {
      dispatch({ type: "SETLOAD", payload: false });
      dispatch({
        type: "SETERR",
        payload: {
          isError: true,
          msg: error.message || "something went wrong",
        },
      });
    }
  };
  useEffect(() => {
    fetchApi("https://dummyjson.com/products");
  }, []);
  console.log(state);
  return (
    <section>
      <div className="head">
        <input type="text" placeholder="please enter some text" />
        <select style={{ marginLeft: "30px" }}>
          <option>please select option</option>
          <option value="A-Z">A-Z</option>
          <option value="Z-A">Z-A</option>
          <option value="low-high">low-high</option>
          <option value="high-low">high-low</option>
        </select>
      </div>
      <div className="items">
        {state.data &&
          state.data.products.map((eachItem) => {
            const {
              id,
              description,
              brand,
              price,
              rating,
              images,
              stock,
              title,
            } = eachItem;
            return (
              <div key={id} className="card">
                <h1>{title}</h1>
                <h1>Brand={brand}</h1>
                <img src={images[0]} alt={id} />
                <h1>price={price}</h1>
                <h1>rating={rating}</h1>
                <h1>stock={stock}</h1>
                <p>{description}</p>
              </div>
            );
          })}
      </div>
    </section>
  );
};
export default Products;
