  return state;
};
const Index = () => {
  const intialState = {
    message: {
      text: "",
      id: "",
    },
    list: [],
  };
  const [state, dispatch] = useReducer(reducer, intialState);
  return (
    <>
      <h1>This is todo List</h1>
      <hr />
      <input
        type="text"
        placeholder="enter some text"
        value={state.message.text}
        onChange={(e) => {
          return {
            ...state,
            message: {
              ...state.message,
              text: e.target.value,
              id: new Date().getTime().toString(),
            },
          };
        }}
      />
      <button>ADD</button>
      <hr />
    </>
  );
};
export default Index;
