import React, { useState } from "react";
import { Table, Pagination } from "react-bootstrap";

const ReusableTable = (data) => {
  console.log(data);
  const [columns, setColumns] = useState(/* define your columns here */);
  const [sortedColumn, setSortedColumn] = useState(null);
  const [sortOrder, setSortOrder] = useState("asc");
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10); // Adjust as needed

  // Add functions for column resizing, reordering, filtering, searching, and hiding/displaying

  // Implement sorting function
  const handleSort = (columnName) => {
    const newSortOrder =
      sortedColumn === columnName
        ? sortOrder === "asc"
          ? "desc"
          : "asc"
        : "asc";
    setSortedColumn(columnName);
    setSortOrder(newSortOrder);
    // Implement sorting logic
  };

  // Implement pagination functions
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = data.slice(indexOfFirstItem, indexOfLastItem);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div>
      <Table striped bordered hover responsive>
        {/* Implement table header with sortable columns */}
        <thead>
          <tr>
            {/* Map through columns and create header cells */}
            {columns.map((col) => (
              <th key={col.name} onClick={() => handleSort(col.name)}>
                {col.label}{" "}
                {sortedColumn === col.name && (
                  <span>{sortOrder === "asc" ? "↑" : "↓"}</span>
                )}
              </th>
            ))}
          </tr>
        </thead>
        {/* Implement table body with data */}
        <tbody>
          {currentItems.map((item, index) => (
            <tr key={index}>
              {/* Map through columns and create table cells */}
              {columns.map((col) => (
                <td key={col.name}>{item[col.name]}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </Table>
      {/* Implement pagination component */}
      <Pagination>
        {Array.from({ length: Math.ceil(data.length / itemsPerPage) }).map(
          (_, index) => (
            <Pagination.Item
              key={index}
              active={index + 1 === currentPage}
              onClick={() => paginate(index + 1)}
            >
              {index + 1}
            </Pagination.Item>
          )
        )}
      </Pagination>
    </div>
  );
};

export default ReusableTable;
