import React,{useState,useEffect} from "react";
const url ="https://jsonplaceholder.typicode.com/users"
const Index=()=>{
    const [users,setUsersdata] = useState([]);
    const [loading,setLoading] = useState(false)
    const [isError,setError] = useState({msg:"",isError:false})
    const fetechUserData =async (url)=>{
        setLoading(true)
        setError({msg:"",isError:false})
        try {
            const response =await fetch(url)
            const data=await response.json()
            setUsersdata(data)
            setLoading(false)
            setError({msg:"",isError:false})
        } catch (error) {
            setLoading(false)
            setError({isError:true,msg:"something went wrong"})
        }
    }
    if(loading){
        return (
            <h1>Loading</h1>
        )
    }
    if(isError.isError){
        return (
            <h1>{isError.msg}</h1>
        )
    }
    useEffect(()=>{
        fetechUserData(url)
    },[])
    return (
        <div>
            <h1>useState-1</h1>
            <ul>
                {
                    users.map((eachUser)=>{
                        const {id,name,email}=eachUser
                        return (
                            <li key={id}>
                                <div>{name}</div>
                                <div>{email}</div>
                            </li>
                        )
                    })
                }
            </ul>
        </div>
    )

}
export default Index;
