import React, { useState } from "react";

const Index = ()=>{
    const tempList = [
        {
            text:"hello",
            id:"eujsdj"
        },
        {
            text:"hello",
            id:"eujseu"
        }
    ]
    const [list,setList] = useState(tempList)
    const [message,setMessage] = useState({
        text:"",
        id:""
    })
    const changeMessage = (e)=>{
        setMessage({
            ...message,
            text:e.target.value
        })
    }
    return (
        <>
            <form>
                <input 
                text="text"
                name="message"
                id="message"
                value={message.text}
                onChange={changeMessage}
                />
                <button type="submit">ADD</button>
                <hr/>
                <ul>
                {
                    list.map((eachItem)=>{
                        const {text,id} = eachItem;
                        return (
                            <li key={id}>
                                <h1></h1>
                            </li>
                        )
                    })
                }
                </ul>
            </form>
        </>
    )
}
export default Index;
