import React, { useReducer } from "react";
const reducer = (state, action) => {
  if (action.type === "CHANGE") {
    return {
      ...state,
      message: {
        ...state.message,
        text: action.paylod,
        id: Date.now().toString(),
      },
    };
  } else if (action.type === "ADD") {
    return {
      ...state,
      list: [...state.list, state.message],
      setItem: function () {
        localStorage.setItem("todo", this.list);
      },
    };
  } else if (action.type === "CLEARADD") {
    return {
      ...state,
      message: {
        ...state.message,
        id: " ",
        text: "",
      },
    };
  } else if (action.type === "DELETE") {
    let newData = state.list.filter((eachItem) => {
      return action.payload !== eachItem.id;
    });
    return {
      ...state,
      list: newData,
    };
  } else if (action.type === "EDIT") {
    let newData = state.list.find((eachItem) => {
      return eachItem.id === action.payload;
    });
    return {
      ...state,
      message: {
        ...state.message,
        text: newData.text,
        id: newData.id,
      },
      edit: {
        ...state,
        id: newData.id,
        isEdit: true,
      },
    };
  } else if (action.type === "EDIT_HANDLER") {
    let newdata = state.list.map((eachItem) => {
      if (action.payload === eachItem.id) {
        return (state.message = {
          ...state.message,
          id: eachItem.id,
          text: state.message.text,
        });
      } else {
        return eachItem;
      }
    });
    return {
      ...state,
      list: newdata,
    };
  }
  return state;
};
const Index = () => {
  const initalState = {
    message: {
      text: "",
      id: "",
    },
    list: localStorage.setItem("todo", []),
    edit: {
      id: "",
      isEdit: false,
    },
    setItem: function () {
      localStorage.setItem("todo", this.list);
    },
    getItem: function () {
      let data = localStorage.getItem("todo")
        ? JSON.parse(localStorage.getItem("todo"))
        : [];
    },
    clearItem: function () {
      localStorage.removeItem("todo");
    },
  };
  const [state, dispatch] = useReducer(reducer, initalState);
  const ADDHandler = () => {
    dispatch({ type: "ADD" });
    dispatch({ type: "CLEARADD" });
  };
  const Deletehandler = (id) => {
    dispatch({ type: "DELETE", payload: id });
  };
  const EditHandler = (id) => {
    dispatch({ type: "EDIT", payload: id });
  };
  const ChangeEdit = () => {
    dispatch({ type: "EDIT_HANDLER", payload: state.edit.id });
  };
  return (
    <>
      <h1>Todo list local storage system</h1>
      <hr />
      <input
        type="text"
        placeholder="Enter some text"
        value={state.message.text}
        onChange={(e) => {
          dispatch({ type: "CHANGE", paylod: e.target.value });
        }}
      />
      {state.edit.isEdit ? (
        <button onClick={ChangeEdit}>EDIT</button>
      ) : (
        <button onClick={ADDHandler}>ADD</button>
      )}
      <hr />
      <ul>
        {state.list.map(({ text, id }) => {
          return (
            <li key={id}>
              <span>{text}</span>
              <button onClick={() => Deletehandler(id)}>DELETE</button>
              <button onClick={() => EditHandler(id)}>EDIT</button>
            </li>
          );
        })}
      </ul>
    </>
  );
};
export default Index;
