import React, { useEffect, useState } from "react";
const ListComp = () => {
  const [Apidata, setData] = useState();
  const [cityData, setCityData] = useState([]);
  const [idvalue, setIdvalue] = useState("");

  const fetchApi = async (url) => {
    try {
      const response = await fetch(url);
      const data = await response.json();
      setData(data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    let url = "https://api.minebrat.com/api/v1/states";
    fetchApi(url);
  }, []);
  function cityHandler(e) {
    setIdvalue(e.target.value);
  }
  function submithandler() {
    if (idvalue) {
      console.log(idvalue);
      let url = `https://api.minebrat.com/api/v1/states/cities/${idvalue}`;
      fetch(url)
        .then((data) => {
          return data.json();
        })
        .then((item) => {
          console.log("item is", item);
          setCityData((pre) => {
            return [...pre, item];
          });
        });
    }
  }

  return (
    <>
      {!Apidata && <h1>loading...</h1>}
      {Apidata && (
        <ul>
          {Apidata.map((eachItem) => {
            const { stateId, stateName, city } = eachItem;
            return (
              <li key={stateId}>
                <h1>{stateId}</h1>
                <h1>{stateName}</h1>
                <select onClick={cityHandler}>
                  {city &&
                    city.map((item) => {
                      return (
                        <option value={item.cityId}>{item.cityName}</option>
                      );
                    })}
                </select>
                <button onClick={submithandler}>submit</button>
                {/* {!cityData && <h1>wait for while......</h1> }
                {cityData && } */}
                <hr />

                <input type="radio" />
              </li>
            );
          })}
        </ul>
      )}
    </>
  );
};
export default ListComp;
