  return state;
};
const Index = () => {
  const initalState = {
    message: {
      text: "",
      id: "",
    },
    list: localStorage.setItem("todo", []),
    edit: {
      id: "",
      isEdit: false,
    },
    setItem: function () {
      localStorage.setItem("todo", this.list);
    },
    getItem: function () {
      let data = localStorage.getItem("todo")
        ? JSON.parse(localStorage.getItem("todo"))
        : [];
    },
    clearItem: function () {
      localStorage.removeItem("todo");
    },
  };
  const [state, dispatch] = useReducer(reducer, initalState);
  const ADDHandler = () => {
    dispatch({ type: "ADD" });
    dispatch({ type: "CLEARADD" });
  };
  const Deletehandler = (id) => {
    dispatch({ type: "DELETE", payload: id });
  };
  const EditHandler = (id) => {
    dispatch({ type: "EDIT", payload: id });
  };
  const ChangeEdit = () => {
    dispatch({ type: "EDIT_HANDLER", payload: state.edit.id });
  };
  return (
    <>
      <h1>Todo list local storage system</h1>
      <hr />
      <input
        type="text"
        placeholder="Enter some text"
        value={state.message.text}
        onChange={(e) => {
          dispatch({ type: "CHANGE", paylod: e.target.value });
        }}
      />
      {state.edit.isEdit ? (
        <button onClick={ChangeEdit}>EDIT</button>
      ) : (
        <button onClick={ADDHandler}>ADD</button>
      )}
      <hr />
      {/* <ul>
        {state.list.map(({ text, id }) => {
          return (
            <li key={id}>
              <span>{text}</span>
              <button onClick={() => Deletehandler(id)}>DELETE</button>
              <button onClick={() => EditHandler(id)}>EDIT</button>
            </li>
          );
        })}
      </ul> */}
    </>
  );
};
export default Index;
