import React, { useState } from "react";
const Index = ()=>{
        const [count,setCount]=useState(0)
        const decrement = ()=>{
            setCount(count--)
        }
       return(
        <React.Fragment>
            <button  onClick={decrement}>-</button>
            <span>{count}</span>
            <button onClick={()=>setCount(count++)}>+</button>
        </React.Fragment>
    )
}
export default Index;
