import React, { useReducer } from "react";
const reducer = (state, action) => {
  if (action.type === "ADD") {
  }
  return state;
};
const Index = () => {
  let intialState = {
    Add: {
      count: 0,
    },
    Sub: {
      count: 0,
    },
    Mul: {
      count: 0,
    },
    Div: {
      count: 0,
    },
    Mod: {
      count: 0,
    },
  };
  const [state, dispatch] = useReducer(reducer, intialState);
  const handleAdd = () => {
    dispatch({ type: "ADD", paylod: 2 });
  };
  return (
    <>
      <h1>{state.Add.count}</h1>
      <button onClick={handleAdd}>+2</button>
      <h1>{state.Sub.count}</h1>
      <button>-2</button>
      <h1>{state.Mul.count}</h1>
      <button>*2</button>
      <h1>{state.Div.count}</h1>
      <button>/2</button>
      <h1>{state.Mod.count}</h1>
      <button>%2</button>
    </>
  );
};
export default Index;
