class Index extends React.Component {
  constructor() {
    super();
    this.state = [{ id: " ", text: " " }, []];
  }
  render() {
    return (
      <>
        <h1>Hello this todo list with class components</h1>
        <hr />
        <input
          type="text"
          placeholder="enter some text"
          onChange={texthandler}
        />
        <button type="submit">ADD</button>
        <hr />
      </>
    );
  }
}

export default Index;
