import React, { useState } from "react";
const Index = ()=>{
    const initialdata = {
        firstname:"Naveen",
        lastname:"Kumar"
    }
    const [data,setData] = useState(initialdata)
    return (
        <>
            <h1>My name is : {data.firstname}</h1>
            <button onClick={()=>setData({
                ...data,
                firstname:"Rocky"
            })}>ChangeFirstname</button>
        </>
    )
}
export default Index;
